import React, { useState, Fragment } from "react";
import gql from 'graphql-tag';

import TodoItem from "./TodoItem";

import {useQuery} from '@apollo/react-hooks';

const GET_LABELED_REVIEWS = gql`
                query getLabeledReviews {
                        pub_review_paragraphs(where:{
                                                      is_labeled:{ _eq: false}},
                                                      limit:10,
                                                      order_by: { date_created: desc})
                                                      {
                                                          id
                                                          review_id
                                                          paragraph_text
                                                          date_created
                                                          is_labeled   
                                                          production_values 
		                                                      how_film_works 
		                                                      enjoyability 
		                                                      why_film_works 
                                                      		effects_on_people 
		                                                      no_production_values 
		                                                      how_film_doesnt_work 
		                                                      no_enjoyability 
		                                                      why_film_doesnt_work 
		                                                      no_effect_on_people 
		                                                      sentiment       
                                                      }
                                }`;


const LabeledReviewsList = props => {
  const [state] = useState({
    filter: "labeled"
  });

  const {todos} = props;
  let filteredTodos = todos;

   if (state.filter === "labeled") {
    filteredTodos = todos.filter(todo => todo.is_labeled !== false);
  }

  const todoList = [];
  filteredTodos.forEach((todo, index) => {
    todoList.push(<TodoItem key={index} index={index} todo={todo} />);
  });

  return (
    <Fragment>
      <div className="todoListWrapper">
        <ul>{todoList}</ul>
      </div>
    </Fragment>
  );
};



const LabeledReviewsListQuery = () => {
  const { loading, error, data } = useQuery(GET_LABELED_REVIEWS);

  if (loading) {
    return <div>Loading...</div>;
  }
  if (error) {
    console.error(error);
    return <div>Error!</div>;
  }
  return <LabeledReviewsList todos={data.pub_review_paragraphs} />;
};

export default LabeledReviewsListQuery
export {GET_LABELED_REVIEWS}
