import React, { useState } from 'react';
import RadioGroup from './RadioGroup'
import {AllRadioOutputContext} from '../../contexts/AllRadioOutputContext/AllRadioOutputContext'


const options = [
  { label: 'Neutral', value: 'neutral' },
  { label: 'Positive', value: 'positive'},
  { label: 'Negative', value: 'negative' }
];

export default function OverallSentimentRadioButtonGroup() {
  const [value, setValue] = useState('neutral');
  const radioContext = useContext(AllRadioOutputContext);


  const onChange = e => {
    setValue(e.target.value);
    if(e.target.value === 'effects_on_people') {
      radioContext.radioDispatch({ type: 'effects_on_people'})
    } else if(e.target.value === 'no_effect_on_people') {
      radioContext.radioDispatch({ type: 'no_effect_on_people'})
    } else if(e.target.value === 'not_applicable') {
      radioContext.radioDispatch({ type: 'no_effect_on_people'})
    }
  };

  return (
    <RadioGroup
      value={value}
      options={options}
      name="sentiment"
      
      onChange={onChange}
    />
  );
}
